'use strict';

let Packet = require('rg').Packet;

let server =
{
SERVER
};

let client =
{
CLIENT
};


function createIndex(object)
{
	let array = [];

	for (let key in object)
	{
		if (!object.hasOwnProperty(key))
			continue;

		array[object[key].id] = object[key];
	}

	return array;
}


module.exports =
{
	server : server,
	client : client,
	indexServer : createIndex(server)
};